css:
body {
          font-family: 'Jomhuria', sans-serif;
          margin: 0;
          padding: 0;
          background-color: #f9f9f9;
      }
      .container {
          width: 80%;
          margin: auto;
          background: white;
          padding: 20px;
          box-shadow: 0 0 10px rgba(0, 0, 0, 0.1);
      }
      .header {
          text-align: center;
          margin-bottom: 40px;
      }
      .header img {
          max-width: 150px;
      }
      .header h1 {
          margin: 0;
          font-size: 24px;
      }
      .header h2 {
          margin: 0;
          font-size: 18px;
          color: #555;
      }
      .content {
          margin-bottom: 40px;
      }
      .content h3 {
          border-bottom: 1px solid #ddd;
          padding-bottom: 10px;
          margin-bottom: 20px;
          font-size: 20px;
      }
      .section {
          margin-bottom: 20px;
      }
      .section h4 {
          margin: 0;
          font-size: 18px;
          color: #333;
      }
      .section .field {
          margin: 5px 0;
      }
      .section .field span {
          font-weight: bold;
          color: #555;
      }
      .footer {
          text-align: center;
          margin-top: 40px;
          font-size: 14px;
          color: #888;
      }
